import React, { useState } from 'react'
import { Jumbotron, Container, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap'

function Cadastrar() {
    const [meta, setMeta] = useState({
        name: '',
        description: '',
        status: '',
    })

    const [response, setResponse] = useState({
        formSave: false,
        type: '',
        message: ''
    })

    const onChangeInput = e => setMeta({ ...meta, [e.target.name]: e.target.value })

    const sendMeta = async e => {
        e.preventDefault()
        console.log(meta)

        setResponse({ formSave: true })
        try {
            const res = await fetch('http://localhost:8080/metas', {
                method: 'POST',
                body: JSON.stringify(meta),
                headers: {
                    'Content-Type': 'application/json'
                }
            })

            const responseEnv = await res.json()
            if (responseEnv.error) {
                setResponse({
                    formSave: false,
                    type: 'error',
                    message: responseEnv.message
                })
            } else {
                setResponse({
                    formSave: false,
                    type: 'success',
                    message: responseEnv.message
                })
            }
        } catch (err) {
            setResponse({
                formSave: false,
                type: 'error',
                message: 'Erro: Meta nao cadastrada com sucesso, tente mais tarde!'
            })
        }
    }


    return (
        <div>
            <>
                <Jumbotron fluid className="form">
                    <style>
                        {`.form{
                            background-color: #171941;
                            color: #bf38ac;
                            padding-top: 30px;
                            padding-bottom: 150px;
                            margin-bottom: 0rem !important;
                        }`}
                    </style>
                    <Container>
                        <h1 className="display-4 text-center">Cadastrar minha meta</h1>
                        <hr />

                        {response.type === 'error' ? <p>{response.message}</p> : ""}
                        {response.type === 'success' ? <p>{response.message}</p> : ""}

                        <Form onSubmit={sendMeta}>

                            <FormGroup>
                                <Label for="name">Nome</Label>
                                <Input type="text" name="Nome" id="Nome" placeholder="Descricao da meta" onChange={onChangeInput} />
                            </FormGroup>
                            <FormGroup>
                                <Label for="name">Nome</Label>
                                <Input type="text" name="Nome" id="Nome" placeholder="Descricao da meta" onChange={onChangeInput} />
                            </FormGroup>

                            <label>Descricao </label>
                            <input type="text" name="description" id="description" placeholder="Descricao da meta"  /> <br /><br />

                            <label>Status </label>
                            <input type="text" name="status" id="status" placeholder="Status da meta" onChange={onChangeInput} /> <br /><br />



                            {response.formSave ? <button type="submit" disabled>Enviando...</button> : <button type="submit">Cadastrar</button>}
                        </Form>
                    </Container>
                </Jumbotron>
            </>
        </div>
    )
}

export default Cadastrar
